import React from 'react';
import ChatMessage from './ChatMessage';
import Signup from './Signup';
import './index.css';

import { default as Chatkit } from '@pusher/chatkit-server';

const chatkit = new Chatkit({
    instanceLocator: "v1:us1:725f4e2d-daa0-46b3-a5fc-15d40bf996e2",
    key: "715a7f69-4ba2-45fe-ae73-b148e6b00444:42JSdX7ZBle1lcpweMkotWn9SAKt74n9Th+A1Zid2Lo="
});

class ChatPage extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			currentView: 'ChatMessage'
		}
		this.changeView = this.changeView.bind(this);
	}

	createUser(username) {
        chatkit.createUser({
            id: username,
            name: username,
        })
        .then((currentUser) => {
            this.setState({
                currentUsername: username,
                currentId: username,
                currentView: 'chatApp'
            })
        }).catch((err) => {
            if(err.status === 400) {
                this.setState({
                    currentUsername: username,
                    currentId: username,
                    currentView: 'chatApp'
                })
                } else {
                    console.log(err.status);
                }
            });
    }

	changeView(view) {
		this.setState({
			currentView: view
		});
	}

	render() {
		let view = '';
		if(this.state.currentView === 'ChatMessage') {
			view = <ChatMessage changeView={this.changeView} />
		}
		else if (this.state.currentView === "signup") {
            view = <Signup />
        }
		return (
            <div className="ChatPage">
            	{view}
            </div>
        );
	}
}

export default ChatPage;
