import React from 'react';
import {connect} from 'react-redux';
import {fetchChannels} from '../actions';
import {Link} from 'react-router-dom';
import nextId from 'react-id-generator';
import './ChannelList.css';
import {Menu, Sidebar} from 'semantic-ui-react';

class ChannelList extends React.Component {
	componentDidMount() {
		this.props.fetchChannels();
	}
	/*
	const PushableSidebar = () => {
		<Sidebar.Pusher>
		<Sidebar
			as={Menu}
			animation='push'
			direction='left'
			icon='labeled'
			inverted
			visible='true'
			vertical
			width='thin'
		>
		</Sidebar>
		</Sidebar.Pusher>
	}
	*/

	renderList() {
		return this.props.channels.map(channel => {
			const id = nextId();
			return (
				<div className="item" key={id}>
					<i className="large middle aligned icon user" />
					<div className="content">
						<div className="description">
							<h2>
								<Link to={`/channels/${channel.id}`} className="header">
									#{channel.name}
								</Link>
							</h2>
						</div>
					</div>
				</div>
			);
		});
	}

	render() {
		return (
			<div className="ui bottom attached segment pushable">
				<div className="ui bottom attached segment pushable">{this.renderList()}</div>
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	return {
		channels: Object.values(state.posts)
	};
};

export default connect(mapStateToProps, {fetchChannels})(ChannelList);
