import React from 'react';
import {connect} from 'react-redux';
import {fetchChannels} from '../actions';
import {Link} from 'react-router-dom';
import nextId from 'react-id-generator';
import {Button, Menu, Sidebar} from 'semantic-ui-react';

class ChannelList extends React.Component {
	state = {
		animation: 'push',
		direction: 'left',
		dimmed: false,
		visible: true
	}

	componentDidMount() {
		this.props.fetchChannels();
	}

	handleAnimationChange = () => {
		const animation = 'push';
		this.setState((prevState) => ({animation, visible: !prevState.visible}))
	}

	renderList() {
		return this.props.channels.map(channel => {
			const id = nextId();
			return (
				<div className="item" key={id}>
					<i className="large middle aligned icon user" />
					<div className="content">
						<div className="description">
							<h2>
								<Link to={`/channels/${channel.id}`} className="header">
									#{channel.name}
								</Link>
							</h2>
						</div>
					</div>
				</div>
			);
		});
	}

	render() {
		return (
			<div className="fp-wrapper">
				<div className="sidebar">
					<Sidebar.Pusher>
						<Sidebar
							as={Menu}
							animation={this.state.animation}
							direction={this.state.direction}
							icon='labeled'
							inverted
							visible={this.state.visible}
							vertical
							width='thin'
						>
						{this.renderList()}
						</Sidebar>
					</Sidebar.Pusher>
					<Button onClick={this.handleAnimationChange}>
						Button
					</Button>
				</div>
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	return {
		channels: Object.values(state.posts)
	};
};

export default connect(mapStateToProps, {fetchChannels})(ChannelList);
