import React from 'react';
import {connect} from 'react-redux';
import {fetchChannel} from '../actions';
import {Link} from 'react-router-dom';

class ChannelList extends React.Component {
	componentDidMount() {
		this.props.fetchChannel();
	}

	renderList() {
		return this.props.channels.map(post => {
			return (
				<div className="item" key={post.id}>
					<i className="large middle aligned icon user" />
					<div className="content">
						<div className="description">
							<h2>
								<Link to={`/posts/${post.id}`} className="header">
									{post.title}
								</Link>
							</h2>
							<p>{post.body}</p>
						</div>
						<div className="header">{post.name}</div>
					</div>
				</div>
			);
		});
	}

	render() {
		return (
			<div>
				<div className="ui relaxed divided list">{this.renderList()}</div>
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	return {
		channels: Object.values(state.channels)
	};
};

export default connect(mapStateToProps, {fetchChannel})(ChannelList);
